mapper.update:
UPDATE Animal SET sexCd = #{sexCd}, noticeNo = #{noticeNo}, kindCd = #{kindCd}, careAddr = #{careAddr}, processState = #{processState} 
				, noticeSdt = #{noticeSdt}, noticeEdt = #{noticeEdt}, weight = #{weight}, careNm = #{careNm}, desertionNo = #{desertionNo} 
				, careTel = #{careTel}, happenPlace = #{happenPlace}, officetel = #{officetel}, orgNm = #{orgNm}, popfile = #{popfile}
				, neuterYn = #{neuterYn}, specialMark = #{specialMark}, colorCd = #{colorCd}, happenDt = #{happenDt}, age = #{age})
WHERE desertionNo = #{desertionNo}
