mapper.select:
SELECT * FROM Board
where BINARY '${param3}' like '%${param4}%'
order by regdate desc limit #{param2} offset #{param1}

    SELECT count(id) FROM BoardView


	       BINARY #{param1} like '%${param2}%'


	    	AND category = #{param3}

mapper.update:
